








#![no_implicit_prelude]
#![allow(warnings)]

include!("include.rs");





#[derive(imp::Unaligned)]
#[repr(u8)]
enum Foo {
    A,
}

util_assert_impl_all!(Foo: imp::Unaligned);

#[derive(imp::Unaligned)]
#[repr(i8)]
enum Bar {
    A,
}

util_assert_impl_all!(Bar: imp::Unaligned);

#[derive(imp::Unaligned)]
#[repr(u8, align(1))]
enum Baz {
    A,
}

util_assert_impl_all!(Baz: imp::Unaligned);

#[derive(imp::Unaligned)]
#[repr(i8, align(1))]
enum Blah {
    B,
}

util_assert_impl_all!(Blah: imp::Unaligned);
