








#![no_implicit_prelude]
#![allow(warnings)]

include!("include.rs");

#[derive(imp::Debug, imp::IntoBytes, imp::Immutable, imp::ByteEq)]
#[repr(C)]
struct Struct {
    a: u64,
    b: u32,
    c: u32,
}

util_assert_impl_all!(Struct: imp::IntoBytes, imp::PartialEq, imp::Eq);

#[test]
fn test_eq() {
    use imp::{assert_eq, assert_ne};
    let a = Struct { a: 10, b: 15, c: 20 };
    let b = Struct { a: 10, b: 15, c: 25 };
    assert_eq!(a, a);
    assert_ne!(a, b);
    assert_ne!(b, a);
}
